import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

const App = () => {
  const [nameQuery, setNameQuery] = useState("");
  const [cityQuery, setCityQuery] = useState("");
  const [colleges, setColleges] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchColleges = async () => {
      if (nameQuery.trim() === "" && cityQuery.trim() === "") {
        setColleges([]); // Clear results if both fields are empty
        setError(null);
        return;
      }

      setLoading(true);
      setError(null);

      try {
        // Construct the API URL with query parameters
        const url = new URL("http://127.0.0.1:5000/college");
        if (nameQuery) url.searchParams.append("name", nameQuery);
        if (cityQuery) url.searchParams.append("city", cityQuery);

        // Make a GET request to the Flask backend
        const response = await fetch(url.toString());
        if (response.ok) {
          const data = await response.json();
          setColleges(data); // Update state with fetched data
        } else {
          const errorMsg =
            response.status === 404
              ? "No colleges found with the specified criteria."
              : "Failed to fetch colleges.";
          setError(errorMsg);
          setColleges([]);
        }
      } catch (err) {
        console.error(err);
        setError("An error occurred while fetching data.");
        setColleges([]);
      } finally {
        setLoading(false);
      }
    };

    fetchColleges(); // Call the fetch function
  }, [nameQuery, cityQuery]); // Re-run when nameQuery or cityQuery changes

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">College Search</h1>
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Search by name"
              value={nameQuery}
              onChange={(e) => setNameQuery(e.target.value)}
            />
          </div>
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Search by city"
              value={cityQuery}
              onChange={(e) => setCityQuery(e.target.value)}
            />
          </div>
        </div>
      </div>
      {loading && (
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      )}
      {error && (
        <div className="alert alert-danger text-center" role="alert">
          {error}
        </div>
      )}
      <div className="row justify-content-center">
        <div className="col-md-8">
          <ul className="list-group">
            {colleges.map((college, index) => (
              <li key={index} className="list-group-item">
                <strong>{college.name}</strong> - {college.city}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default App;
