import React, { useState, useEffect } from "react";

const App = () => {
  const [nameQuery, setNameQuery] = useState("");
  const [cityQuery, setCityQuery] = useState("");
  const [colleges, setColleges] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchColleges = async () => {
      if (nameQuery.trim() === "" && cityQuery.trim() === "") {
        setColleges([]); // Clear results if both fields are empty
        setError(null);
        return;
      }

      setLoading(true);
      setError(null);

      try {
        const url = new URL("http://127.0.0.1:5000/college");
        if (nameQuery) url.searchParams.append("name", nameQuery);
        if (cityQuery) url.searchParams.append("city", cityQuery);
        onsole.log("API URL:", url.toString());
        const response = await fetch(url.toString());
        if (response.ok) {
          const data = await response.json();
          setColleges(data);
        } else {
          const errorMsg =
            response.status === 404
              ? "No colleges found with the specified criteria."
              : "Failed to fetch colleges.";
          setError(errorMsg);
          setColleges([]);
        }
      } catch (err) {
        console.error(err);
        setError("An error occurred while fetching data.");
        setColleges([]);
      } finally {
        setLoading(false);
      }
    };

    fetchColleges();
  }, [nameQuery, cityQuery]);

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h1>College Finder</h1>
      <input
        type="text"
        value={nameQuery}
        onChange={(e) => setNameQuery(e.target.value)}
        placeholder="Search by college name"
        style={{ padding: "10px", width: "100%", marginBottom: "10px" }}
      />
      <input
        type="text"
        value={cityQuery}
        onChange={(e) => setCityQuery(e.target.value)}
        placeholder="Search by city"
        style={{ padding: "10px", width: "100%", marginBottom: "20px" }}
      />
      {loading && <p>Loading...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
      <ul>
        {colleges.map((college) => (
          <li key={college.id}>
            <strong>{college.name}</strong> - {college.location}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
