import React, { useState, useEffect } from "react";

const App = () => {
  const [query, setQuery] = useState("");
  const [colleges, setColleges] = useState([]);
  const [filteredColleges, setFilteredColleges] = useState([]);

  // Fetch data from the Flask API
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://127.0.0.1:5000/colleges");
        const data = await response.json();
        setColleges(data);
        setFilteredColleges(data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  // Handle search and filter colleges
  const handleSearch = (e) => {
    const value = e.target.value.toLowerCase();
    setQuery(value);

    const filtered = colleges.filter((college) =>
      college.name.toLowerCase().includes(value)
    );
    setFilteredColleges(filtered);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>College Filter</h1>
      <input
        type="text"
        placeholder="Search for a college"
        value={query}
        onChange={handleSearch}
        style={{ padding: "10px", width: "300px", marginBottom: "20px" }}
      />
      <ul>
        {filteredColleges.map((college) => (
          <li key={college.id}>
            <strong>{college.name}</strong> - {college.location} ({college.courses} courses)
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
