import React, { useState, useEffect } from "react";

const App = () => {
  const [query, setQuery] = useState("");
  const [colleges, setColleges] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (query) {
      const fetchColleges = async () => {
        try {
          const response = await fetch(`http://127.0.0.1:5000/college?name=${query}`);
          if (response.ok) {
            const data = await response.json();
            setColleges(data);
            setError(null); // Clear any previous errors
          } else {
            setError("No colleges found with that name.");
            setColleges([]); // Clear previous results
          }
        } catch (err) {
          setError("An error occurred while fetching data.");
          setColleges([]);
        }
      };
      fetchColleges();
    }
  }, [query]); // Fetch data whenever query changes

  return (
    <div>
      <h1>College Finder</h1>
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Search for a college"
      />
      {error && <p style={{ color: "red" }}>{error}</p>}
      <ul>
        {colleges.map((college) => (
          <li key={college.id}>
            <strong>{college.name}</strong> - {college.location}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
