import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import Login from "./Login";
import SignUp from "./compsrc/SignUp";

const App = () => {
  const [nameQuery, setNameQuery] = useState("");
  const [cityQuery, setCityQuery] = useState("");
  const [colleges, setColleges] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  // Fetch colleges by name
  const fetchCollegesByName = async () => {
    if (!nameQuery.trim()) return;

    setLoading(true);
    setError(null);

    try {
      const url = new URL("http://127.0.0.1:5000/college");
      url.searchParams.append("name", nameQuery);

      const response = await fetch(url.toString());
      const data = await response.json();

      if (response.ok) {
        setColleges(data);
      } else {
        setError(data.error || "Failed to fetch colleges.");
        setColleges([]);
      }
    } catch (err) {
      console.error(err);
      setError("An error occurred while fetching data.");
      setColleges([]);
    } finally {
      setLoading(false);
    }
  };

  // Fetch colleges by city
  const fetchCollegesByCity = async () => {
    if (!cityQuery.trim()) return;

    setLoading(true);
    setError(null);

    try {
      const url = new URL("http://127.0.0.1:5000/city");
      url.searchParams.append("city", cityQuery);

      const response = await fetch(url.toString());
      const data = await response.json();

      if (response.ok) {
        setColleges(data);
      } else {
        setError(data.error || "Failed to fetch colleges.");
        setColleges([]);
      }
    } catch (err) {
      console.error(err);
      setError("An error occurred while fetching data.");
      setColleges([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (nameQuery) {
      fetchCollegesByName();
    } else if (cityQuery) {
      fetchCollegesByCity();
    } else {
      setColleges([]);
      setError(null);
    }
  }, [nameQuery, cityQuery]);

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">College Search</h1>
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Search by name"
              value={nameQuery}
              onChange={(e) => setNameQuery(e.target.value)}
            />
          </div>
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Search by city"
              value={cityQuery}
              onChange={(e) => setCityQuery(e.target.value)}
            />
          </div>
        </div>
      </div>
      {loading && (
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      )}
      {error && (
        <div className="alert alert-danger text-center" role="alert">
          {error}
        </div>
      )}
      <div className="row justify-content-center">
        <div className="col-md-8">
          <ul className="list-group">
            {colleges.map((college, index) => (
              <li key={index} className="list-group-item">
                <strong>{college.name}</strong> - {college.city}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default App;
